    // ######## START OF INSTRUCTIONS ########
      background(pieceColors[0])  
      // Split the instructions into lines
      let script = instructions.slice().split(/\\r?\\n/)  
      // Set the text appearance
      textFont('Courier') // Courier
      textSize(height/44) // Relative size
      fill(pieceColors[1]) // Either Light or Dark
      // Set a rolling starting index
      let start = frameCount%script.length
      // For each line of the instructions...
      for(let i=0;i<script.length;i++){
        // Take each line...
        let line = script[(start+i)%script.length]
        let textReplacement = ''; // Prepare a variable...
        // Decide on a random set of characters...
        for(let j=0;j<i;j++){ // Loop by the line number
          // Multiply the length of the set by the current line number
          textReplacement += random().toString(36).substr(2, 1)}
        // Replace '//' and '| 's in the line with the random character set
        line = line.replace("//",textReplacement)        
        line = line.replace("| ",textReplacement)
        // Render the line
        text(line,2,-2+(i*(height/35)))}
    // ######## END OF INSTRUCTIONS ########
